import styled from "styled-components";

export const Footer = styled.footer`
  width: 100%;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: #000;
  color: #fff;
`

export const Title = styled.p`
  font-size: 20px;
  font-weight: 700;
`

export const AddressContainer = styled.address`
  font-style: normal;
  font-size: 13px;
  line-height: 25px;
`
