//   top: 0;
//   right: 0;
//   border-bottom: 80px solid transparent;
//   border-right: 80px solid #f8475f;
// `

export const OfferTag = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  div {
    border-bottom: 80px solid transparent;
    border-right: 80px solid #f8475f;
  }
  span {
    color: #fff;
    font-size: 16px;
    font-weight: 900;
  }


`
