import styled from "styled-components";

export const CarouselContainer = styled.div`
  width: 100%;
  & .slick-dots {
    bottom: 37px;
    & .slick-active {
      button {
        background-color: #f8475f;
      }
    }
    button {
      width: 10px;
      height: 10px;
      /* border-radius: 50%; */
      background-color: #bdbdbd;
      &::before {
        content: "";
      }
    }
  }
`



// slide style
export const Slide = styled.div`
  width: 100%;
  img {
    width: 100%;
  }
`
