import styled from "styled-components";

export const CarouselContainer = styled.div`
  width: 100%;
  & .slick-dots {
    bottom: 37px;
    & .slick-active {
      button {
        background-color: #f8475f;
      }
    }
    button {
      width: 10px;
      height: 10px;
      border-radius: 50%;
      background-color: #bdbdbd;
      ::before {
        content: "";
      }
    }
  }
`


// slide style
export const Slide = styled.div`
  position: relative;
  width: 100%;
  color: #fff;
  img {
    width: 100%;
  }
  div {
    position: absolute;
    top: 30%;
    left: 5%;
    :nth-child(1) {
      font-size: 30px;
      font-weight: 700;
      /* margin-bottom: 20px; */
    }
    :nth-child(2) {
      font-size: 45px;
      font-weight: 900;
    }
  }
`
