import React from 'react'
import Stars from "../Stars";
import { toLocalCurrency } from "../../utils";
import * as S from "./styles";

export default function ProductCard({product}) {



  return (
    <S.Card>
      <S.ImgWrapper>
        <img src={product.imageUrl} alt="produto" />
        {product.listPrice ? (
          <S.OfferTag> 
            <div/>
            <span>OFF</span>
          </S.OfferTag>
        ) : null}
      </S.ImgWrapper>
      <div>
        <S.Title>{product.productName}</S.Title>
        <Stars starsReceived={product.stars}/> 
        <p> {toLocalCurrency(product.listPrice)}</p>
        <p> {toLocalCurrency(product.price)} </p>
        <p>
          { 
            product.installments.length 
            ? `ou em ${product.intallments[0].quantity}
              x de ${toLocalCurrency(product.intallments[0].value)} ` 
            : ''
          }
        </p>
      </div>
      <button> COMPRAR </button>
    </S.Card>
  )
}
