import React, { useState } from 'react'
import { emailRegex, BASE_URL } from "../../utils";
import axios from "axios";

export default function NewsletterForm () {
  const [data, setData] = useState({})
  const [errors, setErrors] = useState({})
  const [showForm, setShowForm] = useState(true)


  const validateForm = () => {
    data.name?.length > 3 
      ? setErrors.name('') 
      : setErrors.name('Preencha com seu nome completo')
    emailRegex.test(data.email) 
      ? setErrors.email('') 
      : setErrors.email('Preencha com um e-mail válido')
    console.log(errors)
    return errors.name?.length || errors.email?.length ? true : false
  }

  const submit = (e) => {
    e.preventDefault()
    console.log(data)
    if (validateForm) return

    axios.post(`${BASE_URL}newsletter`, data)
      .then(res => {
        console.log(res.data)
        setData({})
        setShowForm(false)
      })
      .catch(err => console.log(err))
  }

  return (
    <>
      {showForm ? (
        <div>
          <h3>
            Participe de nossas news com promoções e novidades!
          </h3>
          <form onSubmit={submit}>
            <div>
              <input 
                type="text" 
                placeholder="Digite seu nome" 
                onChange={e => setData.name(e.target.value)}
              />
              <span> {errors.name} </span>
            </div>
            <div>
              <input 
                type="email" 
                placeholder="Digite seu email" 
                onChange={e => setData.email(e.target.value)}
              />
              <span> {errors.email} </span>
            </div>
            <button type="submit">Eu quero!</button>
          </form>
        </div>
      ) : (
        <div>
          <h3>Seu e-mail foi cadastrado com sucesso!</h3>
          <h4>A partir de agora você receberá as novidades e ofertas exclusivas.</h4>
          <button onClick={() => setShowForm(true)}>Cadastrar novo e-mail</button>
        </div>        
      )}
    </>
  )
}
